import collections


def longestPalindrome(s):
    dic = collections.Counter(s)

    waitList = []
    counter = 0
    for key in dic.keys():
        if dic[key] % 2 == 0:
            counter += dic[key]
        else:
            waitList.append(dic[key])

    if len(waitList) == 0:
        return counter

    print(counter)
    print(waitList)
    for count in waitList:
        counter += count - 1

    print(counter)


s = ""
print(longestPalindrome(s))